create DATABASE nyc_taxi_ldw;

 ALTER DATABASE nyc_taxi_ldw COLLATE Latin1_General_100_CI_AI_SC_UTF8;

create SCHEMA bronze;
create SCHEMA silver;
create SCHEMA brgoldonze;

-- CREATE EXTERNAL DATA SOURCE TO ACCESS CONTAINER FILES
CREATE EXTERNAL DATA SOURCE nyc_taxi_src
WITH(
    LOCATION = 'https://synapsetaxistorage.dfs.core.windows.net/taxifiles',

);


    -- CREATE EXTERNAL TABLE
IF OBJECT_ID ('bronze.taxi_zone') IS NOT NULL
    DROP EXTERNAL TABLE bronze.taxi_zone;

    CREATE EXTERNAL TABLE bronze.taxi_zone(
        LocationID SMALLINT,
        Borough VARCHAR(15),
        Zone VARCHAR(50),
        service_zone VARCHAR(15)
    )
    WITH(
        LOCATION = 'raw/taxi_zone.csv',
        DATA_SOURCE = nyc_taxi_src,
        FILE_FORMAT = csv_file_format,
        REJECT_VALUE = 10,
        REJECTED_ROW_LOCATION = 'rejections/taxi_zone'
    )


SELECT * FROM bronze.taxi_zone;
